'use strict'

var phantom = require('phantom')

var _ph, _page

phantom.create().then(function (ph) {
  _ph = ph
  return _ph.createPage()
}).then(function (page) {
  _page = page
  _page.property('onConsoleMessage', function (msg) {
    console.log('CONSOLE: ' + msg)
  })
  _page.property('onError', function (msg) {
    console.log('phantomjs page error : ' + msg)
  })
  /*
  _page.property('onResourceReceived', function (msg) {
    console.log('received : ' + msg)
  })
  */
  return _page
}).then(function (page) {
  return _page.open('http://localhost:9001/tester.html')
}).then(function (status) {
  console.log(status)
  if (status === 'fail') throw 'Connection Failure'
  return
}).then(function () {
  return _page.evaluate(function () {
    return $('button').click()
  })
}).then(function (next) {
  // waiting for the websocket to connect
  setTimeout(function () {
    _page.close()
    _ph.exit()
  }, 10000)
}).catch(function (error) {
  console.log(error)
  _page.close()
  _ph.exit()
})
