function makePalindrome(text) {
  var rev = text.split('').reverse().join('');
  for (var i = text.length; i >= 0; i--) {
    if (rev.slice(0, i) === text.slice(-i)) {
      return text + rev.slice(i);
    }
    if (text.slice(0, i) === rev.slice(-i)) {
      return rev + text.slice(i);
    }
  }
}
