function checkCashRegister(price, cash, cid) {
  var changeRequired = cash - price;
  var change = [];
  var note = 0.00;
  var cidRemaining = 0.00;
  var addNewNoteFlag = true;

  const notesNames = ['PENNY', 'NICKEL', 'DIME', 'QUARTER', 'ONE', 'FIVE', 'TEN', 'TWENTY', 'ONE HUNDRED'];
  const notesNumbers = [0.01, 0.05, 0.10, 0.25, 1.00, 5.00, 10.00, 20.00, 100.00];

  // loop from highest cid denomination backwards (starting at 100 then down)
  for (let i = cid.length - 1; i >= 0; i--) {
    // determine the denomination of currently selected cid note in numeric value
    note = notesNumbers[notesNames.indexOf(cid[i][0])];

    // check note is smaller than changeRequired and still notes in the drawer
    while (Math.round(changeRequired * 100) / 100 >= note && cid[i][1] > 0.00) {
      // add array on to change if the note hasnt been used before
      if (addNewNoteFlag) change.push([cid[i][0], 0.00]);
      addNewNoteFlag = false;
      // take note of the money moved in iteration:
      change[change.length - 1][1] += note;
      cid[i][1] -= note;
      changeRequired -= note;
    }
    // cidRemaining checks whether till has "Closed"
    cidRemaining += cid[i][1];
    addNewNoteFlag = true;
  }

  // check for "Insufficient Funds" or "Closed"
  if (Math.round(changeRequired * 100) / 100 > 0.00) {
    return 'Insufficient Funds';
  } else if (Math.round(cidRemaining * 100) / 100 === 0.00) {
    return 'Closed';
  }

  // Otherwise return change array
  return change;
}

