var twitchApp = angular.module('twitchApp', []);

twitchApp.controller('mainController',function($scope,$http){
  var users = ["freecodecamp", "storbeck", "terakilobyte","habathcx","RobotCaleb","thomasballinger","noobs2ninjas","beohoff","brunofin","comster404 "]; 
  var usersapi = "https://api.twitch.tv/kraken/channels/";
  var dummy_image = "https://dummyimage.com/50x50/ecf0e7/5c5457.jpg&text=0x3F";
  var callback = "?callback=JSON_CALLBACK";
  $scope.results = [];

  $scope.getResults = function() {
    for(var i=0; i<users.length; i++){
      var user = users[i];
      // console.log(user);

      $http.jsonp(usersapi+users[i]+callback)
        .then(function successCallback(response){
              if(response.data.status === 422){
                console.log(users[i]);
              }
              if(response.data.logo === null){
                  response.data.logo = dummy_image;
              }        
              var result = {"img":response.data.logo, "display_name":response.data.display_name};
              $scope.results.push(result);
      }, function errorCallback(response) {
        console.log(response);
      });
    }
  } // end of getResults

  $scope.getResults();
});

