function findLongestWord(str) {
    var temp = str.split(" "),
        x = [];
    for (var i = 0; i <temp.length; i++){
        x.push(temp[i].length);

    }
    x.sort(function(a,b){
        return b-a;
    });
    for(var j = 0; j < temp.length; j++){
        if(temp[j].length === x[0] ){
            return temp[j];
        }
    }
}
findLongestWord('The quick brown fox jumped over the lazy dog');
```

