function sumPrimes(num){
  var isPrime = "1".repeat(num+1).split('');
  isPrime[0] = 0;
  isPrime[1] = 0;

  for(var p = 2; p < isPrime.length; p++){
    if(isPrime[p] != '1')
      continue;

    for(var j = 2; p*j < isPrime.length; j++)
      isPrime[p*j] = 0;
  }
  return isPrime.reduce(function(acc,v,i){
    return acc + (v === '1' ? i : 0)
  });
}
