function makeFriendlyDates(arr) {
  var MONTHS = ("January,February,March,April,May,June,July," +
                "August,September,October,November,December").split(",");

  var MyDate = function(info){
    this.day = Number.parseInt(info[2]);
    this.year = Number.parseInt(info[0]);
    this.month = Number.parseInt(info[1]);
    this.ordDay = this.day%10 > 3 || [11,12,13].indexOf(this.day) >= 0?
      this.day + 'th' : this.day + ['th','st','nd','rd'][this.day%10];
    this.monthName = MONTHS[this.month-1];
    this.hash = (this.year<<10) + (this.month<<5) + this.day; //base 32 hash
  };

  var date1 = new MyDate(arr[0].split("-"));
  var date2 = new MyDate(arr[1].split("-"));

  var isWithinYear = date2.hash - date1.hash < 32*32;
  var isCurYear = new Date().getFullYear() === date1.year;

  var beginYear = isCurYear && isWithinYear? "" : ", " + date1.year;
  var beginMonth = date1.monthName + " ";

  var endYear = isWithinYear? "" : ", " + date2.year;
  var endMonth = isCurYear && date1.month === date2.month ? "" : date2.monthName + " ";

  return date1.hash === date2.hash?
    [beginMonth + date1.ordDay + beginYear] :
    [beginMonth + date1.ordDay + beginYear, endMonth + date2.ordDay + endYear];
}
