function translatePigLatin(str) {
  var vowels = ['a', 'e', 'i', 'o', 'u'];

  var firstLetter = str.substring(0, 1);
  if(vowels.indexOf(firstLetter.toLowerCase()) >= 0) {
    // word starts with vowel
    return str + 'way';
  }
  else {
    var firstVowelPlace;

    // finds the first occurence of a vowel
    str.split("").reduce(function(previous, currentCharacter, index) {
      if(!firstVowelPlace && vowels.indexOf(currentCharacter.toLowerCase()) >= 0) {
        firstVowelPlace = index;
      }
    });

    return str.substring(firstVowelPlace, str.length) + str.substring(0, firstVowelPlace) + 'ay';
  }

}

