function convertToRoman(num) {
  var symbols = ['I', 'V', 'X', 'L', 'C', 'D', 'M'];
  var values = [1, 5, 10, 50, 100, 500, 1000];
  var offOneSymbols = ['', 'I', 'I', 'X', 'X', 'X', 'C', 'D'];
  var offOne = [0,1,1,10,10,10, 100, 500]; 

  function loop(result, num, i){
    if(num <= 0)
      return result;
    if(values[i] <= num)
      return loop(result + symbols[i], num - values[i], i);
    if(values[i] - offOne[i] <= num)
      return loop(result + offOneSymbols[i] + symbols[i], num - values[i] + offOne[i], i-1);

    return loop(result, num, i-1);
  }

  return loop('', num, symbols.length);
}

