/* ======= Title Case a Sentence ======= */
// Version 1
titleCase("I'm a little tea pot");

function titleCase(str) {

  var splitted = str.split(' ');
  var alexsArray = [];

  for(var i = 0; i<splitted.length; i++){
      var placeHolder = splitted[i];
      placeHolder=placeHolder.charAt(0).toUpperCase() + placeHolder.slice(1).toLowerCase();
      alexsArray.push(placeHolder);
  }
  return alexsArray.join(' ');
}

titleCase("I'm a little tea pot");

// Version 2
function titleCase(str) {

  var splitted = str.split(' ')

  var titleSplitted = splitted.map(function (word) {
      return word[0].toUpperCase() + word.slice(1).toLowerCase()
  })

  var titleCased = titleSplitted.join(' ')

  return titleCased
}

titleCase("I'm a little tea pot");

// Version 3
function titleCase(str) {

  return str
    .split(' ')
    .map(function (word) {
        return word[0].toUpperCase() + word.slice(1).toLowerCase()
    })
    .join(' ')
}

