function titleCase(str) {
  str = str.toLowerCase();
  var strArr = str.split(" ");
  for(var i = 0; i < strArr.length; i++) {
    strArr[i] = strArr[i].charAt(0).toUpperCase() + strArr[i].slice(1);
    }
  str = strArr.join(" ");
  return str;

}
