function where(collection, source) {
  var arr = [];
  for(var i=0, l=collection.length; i<l; i++){
    for(var key in source) {
      if(typeof collection[i][key] !== 'undefined' && collection[i][key] == source[key]) arr.push(collection[i]);
    }
  }
  return arr;
