function where(collection, source) {
  var arr = [];
  var item = Object([source]);

  /* has item from list the 'last' property */
  for( item in collection ){   
    if ( item.hasOwnProperty('last') ){       
      Object.keys(item);  
      arr.push(item);
    }
  }
  // What's in a name?
  return item;
}

