function steamroller(arr) {
    var newArr = [];

    helper(arr);
    function helper (arr) {
    arr.forEach(function (value) {
        if (!Array.isArray(value)) {
            newArr.push(value);
        } else {
            helper(value);
        }
    });
   }
  return newArr;
}
