function sumFibs(num) {
    var fibs = [0, 1];
    for (i = 2; fibs[i - 1] < num; i++) {
        fibs[i] = fibs[i - 1] + fibs[i - 2];
    }
    var sum = 0;
    console.log(fibs);
    for (i = 0; i < fibs.length - 1; i++) {
        if (fibs[i] % 2 !== 0) {
            sum += fibs[i];
        }
    }
    return sum;
}
sumFibs(4000000)
