function mutation(arr) {
  var str1 = arr[1].toLowerCase();
  var str0 = arr[0].toLowerCase();
  var count = 0;
  var pos = 0;
  for (var i = 0; i < str1.length; i++) {
    pos = str0.indexOf(str1[i]);

    while (pos !== -1) {
      count++;
      pos = str0.indexOf(str1[i], pos + 1);
    }
  }
  //return count;

  if (count === str1.length) {
    return true;
  }
  return false;

}

mutation(["hello", "Hello"]);
//mutation(["floor", "for"]);
```
