int get_fibonacci_last_digit(int n) {
	//write your code here
	if (n <= 1)
	{
		return n % 10;
	}
	else
	{
		vector<unsigned long long> fibCal;
		fibCal.resize(n + 1);
		fibCal.at(0) = 0;
		fibCal.at(1) = 1;
		for (int i = 2; i <= n; i++)
		{
			fibCal.at(i) = (fibCal.at(i - 1) % 10) + (fibCal.at(i - 2) % 10);
			// to count the reminder together 
		}

		return fibCal[n] % 10;
	}
}
