	{
		return n % 10;
	}
	else
	{
		vector<long> fibCal;
		fibCal.resize(n + 1);
		fibCal.at(0) = 0;
		fibCal.at(1) = 1;
		for (int i = 2; i <= n; i++)
		{
			fibCal.at(i) = fibCal.at(i - 1) + fibCal.at(i - 2);
		}

		return fibCal[n] % 10;
	}
}

