package Sorting;

public class MergeSort {
	static int[] readArray(){
		int[] a ={ 23,1,127,11,19,16,32};
		return a;
	}

	static void printArray(int[] array){
		for(int i=0; i<=array.length; i++){
			System.out.println(array[i]);
		}
	}
	static void sort(int[] input){
		mergeSort(input,0,input.length-1);
	}

	static void mergeSort(int[] array,int lowerIndex,int higherIndex){
		if(lowerIndex<higherIndex){
			int middle = (lowerIndex-higherIndex)/2;
			mergeSort(array,lowerIndex,middle);
			mergeSort(array,middle+1,higherIndex);
			merge(array,lowerIndex,middle,higherIndex);
		}
	}

	static void merge(int[] array,int lowerIndex,int middle, int higherIndex){
		int[] temp = new int[higherIndex-lowerIndex+1];
		int i = lowerIndex;
		int j = middle+1;
		int k = lowerIndex;
		while(i<=middle && j<=higherIndex){
			if(array[i]<array[j]){
				temp[k]=array[i];
				i++;
			}
			else{
				temp[k]=array[j];
				j++;
			}
		}

		if(i<middle){
			temp[k]=array[i];
			k++;
			i++;
		}
		else{
			temp[k]=array[j];
			j++;
			i++;
		}
		for(int m=0; m<temp.length;m++ ){
			array[m+lowerIndex]=temp[m];
			m++;
		}
	}
	public static void main(String[] args) {
		int[] inputArray = readArray();
		printArray(inputArray);
		sort(inputArray);
		System.out.println("Sorted Array:");
		printArray(inputArray);

	}

}

